<div class="comment" id="c<?php echo $data->id; ?>">    <?php    echo CHtml::link("#{$data->id}", $data->url, array(        'class' => 'cid',        'title' => 'Permalink to this comment',    ));    ?>    <?php echo $data->grup; ?>    <div class="author">        <?php echo $data->authorLink; ?>         <?php echo CHtml::link(CHtml::encode($data->post->title), $data->post->url); ?>    </div>    <div class="time">        <?php if ($data->status == Comment::STATUS_PENDING): ?>            <span class="pending">В ожидании утверждения</span> |            <?php            echo CHtml::linkButton('Подтвердить', array(                'submit' => array('comment/approve', 'id' => $data->id),            ));            ?> |        <?php endif; ?>        <?php        if (Yii::app()->user->getRole() == 'administrator' || Yii::app()->user->getRole() == 'authority') {            echo CHtml::linkButton('Удалить', array(                'submit' => array('comment/delete', 'id' => $data->id),                'confirm' => "Вы действительно хотите удалить коментарий #{$data->id}?",            ));        }        ?>        <?php        ?> |        <?php echo date('F j, Y \a\t h:i a', $data->create_time); ?>    </div>    <div class="content">        <?php echo nl2br(CHtml::encode($data->content)); ?>    </div></div><!-- comment -->